<script>
  import { onMount } from "svelte";
  import { browser } from "$app/environment";
  import SplitType from "split-type";

  let gsap;
  let ScrollTrigger;

  onMount(async () => {
    if (browser) {
      gsap = (await import("gsap")).default;
      ScrollTrigger = (await import("gsap/ScrollTrigger")).default;

      gsap.registerPlugin(ScrollTrigger);

      const text = new SplitType(".intro", { types: "lines" });

      gsap.set(text.lines, {
        y: "100%",
        opacity: 0,
      });

      ScrollTrigger.create({
        trigger: ".intro",
        start: "bottom bottom",
        end: "bottom -300%",
        // markers: true,
        onEnter: () => animateText(text.lines),
        onEnterBack: () => animateText(text.lines),
        onLeave: () => resetText(text.lines),
        onLeaveBack: () => resetText(text.lines),
      });

      function animateText(elements) {
        gsap.to(elements, {
          y: "0%",
          opacity: 1,
          duration: 1,
          stagger: 0.1,
          ease: "power2.out",
        });
      }

      function resetText(elements) {
        gsap.set(elements, {
          y: "100%",
          opacity: 0,
        });
      }
    }
    gsap.to(".sun-svg ", {
      rotation: 360,
      duration: 3, // Adjust duration for desired speed
      repeat: -1, // Infinite loop
      ease: "linear", // Smooth continuous rotation
      transformOrigin: "50% 50%",
    });
    gsap.to(".world-picto", {
      rotationY: 360,
      duration: 20, // Adjust duration for desired speed
      repeat: -1, // Infinite loop
      ease: "linear", // Smooth continuous rotation
      transformOrigin: "50% 50%",
      yoyo: false, // Continuous rotation
    });
    // GSAP Animation Script
    gsap.to("#haltere", {
      y: 20, // Move 20 pixels up and down
      repeat: -1, // Repeat indefinitely
      yoyo: true, // Move back and forth
      duration: 1, // Duration of each animation cycle
      ease: "power1.inOut", // Easing function for smooth motion
    });
    gsap.fromTo(
      ".www",
      { x: "100vw" }, // Start position (off-screen to the right)
      {
        x: "-100vw", // End position (off-screen to the left)
        duration: 5, // Duration of the animation
        repeat: -1, // Repeat infinitely
        ease: "none", // Linear motion
      }
    );
    gsap.fromTo(
      "#Curseur",
      {
        x: 0,
        y: 0,
      },
      {
        x: 100, // Move right
        y: 0,
        duration: 1,
        ease: "none",
        onComplete: function () {
          gsap.to("#Curseur", {
            x: 100,
            y: 100, // Move down
            duration: 1,
            ease: "none",
            onComplete: function () {
              gsap.to("#Curseur", {
                x: 0, // Move left
                y: 100,
                duration: 1,
                ease: "none",
                onComplete: function () {
                  gsap.to("#Curseur", {
                    x: 0,
                    y: 0, // Move up
                    duration: 1,
                    ease: "none",
                    onComplete: function () {
                      // Loop the animation
                      gsap.fromTo(
                        "#Curseur",
                        { x: 0, y: 0 },
                        { x: 100, y: 0, duration: 1, ease: "none" }
                      );
                    },
                  });
                },
              });
            },
          });
        },
        repeat: -1, // Repeat infinitely
        yoyo: false, // Do not alternate
      }
    );
  });
</script>

